{
    options = {unused: true};
    input: {
        function f(x, y) {
            // circular reference
            function g() {
                return h();
            }

            function h() {
                return g();
            }

            return x + y;
        }
    }
    ;
    expect: {
        function f(x, y) {
            return x + y;
        }
    }
}

{
    options = {unused: true};
    input: {
        function f(x, y) {
            var foo = 1, bar = baz, baz = foo + bar, qwe = moo();
            return x + y;
        }
    }
    ;
    expect: {
        function f(x, y) {
            moo();              // keeps side effect
            return x + y;
        }
    }
}

{
    options = {unused: true};
    input: {
        function f(x, y) {
            var g = function () {
                return h()
            };
            var h = function () {
                return g()
            };
            return x + y;
        }
    }
    ;
    expect: {
        function f(x, y) {
            return x + y;
        }
    }
}
